package knight;public class Helmet extends Ammunition {    private String typeofarmoring;    public Helmet(double cost, double weight, String material,double strength,String typeofarmoring) {        super(cost, weight, material,strength);        this.typeofarmoring = typeofarmoring;    }    public String getTypeofarmoring() {        return typeofarmoring;    }    public void setTypeofarmoring(String typeofarmoring) {        this.typeofarmoring = typeofarmoring;    }    @Override    public double ReturnCost()    {        return 3.0;    }}